var distanceObj = {
    distanceCalculator: function(pointObj, targetObj){
        pointLatitude = Number(targetObj.latitude); //res
        targetLatitude = Number(pointObj.latitude);
        pointLongitude = Number(targetObj.longitude);
        targetLongitude = Number(pointObj.longitude);
        let radlat1 = Math.PI * targetLatitude / 180
        let radlat2 = Math.PI * pointLatitude / 180
        let theta = targetLongitude - pointLongitude
        let radtheta = Math.PI * theta / 180
        let dist = Math.sin(radlat1) * Math.sin(radlat2) + Math.cos(radlat1) * Math.cos(radlat2) * Math.cos(radtheta);
        dist = Math.acos(dist)
        dist = dist * 180 / Math.PI
        dist = dist * 60 * 1.1515
        dist = dist * 1.609344
        return dist;
    }
}

module.exports = distanceObj;